import React from 'react';
import { request } from '../utils/axios'
import { withRouter } from "react-router";

class ClientsForm extends React.Component {
  state = {
    client: '',
    project: '',
    department: '',
    estimate: '',
    budget: '',
    start_date: ''
  }

  handleClientChange = event => {this.setState({ client: event.target.value })}
  handleProjectChange = event => {this.setState({ project: event.target.value })}
  handleDepartmentChange = event => {this.setState({ department: event.target.value })}
  handleEstimateChange = event => {this.setState({ estimate: event.target.value })}
  handleBudgetChange = event => {this.setState({ budget: event.target.value })}
  handleStartDateChange = event => {this.setState({ start_date: event.target.value })}

  handleSubmit = event => {
    event.preventDefault();

     request.post('/api/clients', { client: this.state.client, project: this.state.project, department:this.state.department,
    estimate: this.state.estimate, budget: this.state.budget, start_date: this.state.start_date},)
     .then(response => {
       console.log(response)
       this.props.history.push('/clients')
     })
     .catch(function (err) {
       console.log(err.response);
     });
   }
   render() {
    return (
      <div>    
        <form onSubmit={this.handleSubmit}>
            <input 
            placeholder="Name"
            value={this.state.client}
            required type="text" 
            name="client"  
            onChange={this.handleClientChange} />

            <input 
            placeholder="Project"
            value={this.state.project} 
            required type="text"
            name="project"  
            onChange={this.handleProjectChange} />

            <select 
             value={this.state.department}
             value={this.state.value}  
             onChange={this.handleDepartmentChange}>
            <option value="" disabled selected>Department</option>
            <option value="1">Milestep</option>
            <option value="2">LibidoSoft</option>
            </select>

            <input 
            placeholder="Est"
            value={this.state.estimate} 
            required type="text"
            name="estimate"  
            onChange={this.handleEstimateChange} />

            <input 
            placeholder="Bdjt"
            value={this.state.budget} 
            required type="text"
            name="budget"  
            onChange={this.handleBudgetChange} />

            <input 
            placeholder="Start Date"
            value={this.state.start_date} 
            required type="text"
            name="startDate"  
            onChange={this.handleStartDateChange} />
          <button type="submit">Create</button>
        </form>
      </div>
    )
  }
}
export default withRouter(ClientsForm);
